import numpy as npimport matplotlib.pyplot as pltfilename = "thermal.out"data = np.loadtxt(filename)fig = plt.figure()ax = fig.add_subplot(1, 1, 1)plt.plot(data[:,0], data[:,1], label='umu = 0.1')plt.plot(data[:,0], data[:,2], label='umu = 0.2')plt.plot(data[:,0], data[:,3], label='umu = 0.3')plt.plot(data[:,0], data[:,4], label='umu = 0.4')plt.plot(data[:,0], data[:,5], label='umu = 0.5')plt.plot(data[:,0], data[:,6], label='umu = 0.6')plt.plot(data[:,0], data[:,7], label='umu = 0.7')plt.plot(data[:,0], data[:,8], label='umu = 0.8')plt.plot(data[:,0], data[:,9], label='umu = 0.9')plt.plot(data[:,0], data[:,10], label='umu = 1.0')plt.legend()plt.xlabel('Wavelangth (nm)')plt.ylabel('Radiance')plt.title('Radiance \nSource: thermal, rte_solver: disort')